import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {Card, Grid} from '@material-ui/core'
import { Header } from 'tabler-react';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import CenteredImage from '../_Modules/centeredImage';
const data = require('../../../data/ppb.json')

const useStyles = makeStyles((theme) => ({
  title: {
    fontWeight: 'bold',
    fontSize: '30px',
    paddingTop: '30px'
  },
  date: {
    fontWeight: 'bold',
    fontSize: '23px',
  },
  text: {
    flexWrap: 'wrap',
    maxWidth: '260px',
    margin: '0 -5rem 1rem -5rem'
  }
}));

export default function PnPB(props) {
  const classes = useStyles();
  return (
    <Box marginBottom={1.5}>
      <Grid container spacing={3}>
        {data.map(event => (
          <Grid xs={6} padding={3}>
            <Box padding={1.5}>
              <Card elevation={0} align={'center'} width={400}>
                <Box height={250} marginTop={'25px'}>
                  <CenteredImage height={180} src={event['Image']} />
                </Box>
                <Box height={150}>
                  <Typography className={classes.date}>
                    {event['Date']}
                  </Typography>
                </Box>
              </Card>
            </Box>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
