import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Grid } from '@material-ui/core';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import TableContainer from '@material-ui/core/TableContainer';
import Table from '@material-ui/core/Table';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
import TableBody from '@material-ui/core/TableBody';
import Card from '@material-ui/core/Card';
const ceKey = require('../../../data/education.json')

const useStyles = makeStyles((theme) => ({
  date: {
    fontSize: 30
  },
  course: {
    fontSize: 24
  }
}));

export default function UpcomingEvent(props) {
  const classes = useStyles();
  let { ce2020, ce2021 } = props;
  const [upcomingEvents, setUpcomingEvents] = useState([])
  const [mappedEvent, setMappedEvent] = useState({})

  useEffect(() => {
    let selectedEvents = ce2021.filter(event => {
      return event['Status'] != 'Completed' && !event['Status'].includes('Can')
    })
    setUpcomingEvents(selectedEvents)
    if(selectedEvents){
      setMappedEvent(ceKey.find(event => event['Event Map'] === selectedEvents[0].course))

    }
  })
  const sortByMonth = (arr) => {
    return arr.sort((a, b) => {
      return a['month'] - b['month'];
    });
  };

  ce2020 = sortByMonth(ce2020);
  ce2021 = sortByMonth(ce2021);

  let pastEvents = ce2020.concat(ce2021)
  pastEvents = pastEvents.filter(event => {
    return event['Status'].includes('Comp')
  })
  pastEvents = pastEvents.sort((a, b) => {
    return b['month'] - a['month'];
  })

  let completed20 = ce2020.filter(event => event['Status'].includes('Comp')).length
  let completed21 = ce2021.filter(event => event['Status'].includes('Comp')).length



  return (
    <Grid >
      {upcomingEvents.length ? (
        <Box marginBottom={'-.8rem'} with={'100%'}>
          <Card width={'100%'} elevation={0}>


          <Box marginTop={2} width={'100%'} height={200}>
            <Typography className={classes.date} align={'center'}>
              {upcomingEvents[0]['Date']}
            </Typography>
            <Grid container>
              <Grid item xs={4}>
                <Typography align={'center'} className={classes.course}>{mappedEvent ? mappedEvent['Title'] : ''}</Typography>
              </Grid>
            </Grid>
          </Box>
          </Card>

        </Box>
      ) : null}
    </Grid>
  );
}
