import React, { useEffect, useState } from 'react';
import { Grid } from '@material-ui/core';
import { DataBox } from '../DataBox';
import { valueKeyText, calculatePercentChange, printPercent, formatPercentAsString } from '../../DataFunctions';

export default function RetroDataGrid(props) {
  const [cardData, setCardData] = useState([])
  const {data} = props;

  useEffect(() => {
    let finalMonth = data.filter(x => Number(x['Date'].split(' ')[1]) === 2020).reverse()[0]
    let comparableMonth = data.filter(x => Number(x['Date'].split(' ')[1]) === 2019).reverse()[0]

    let _cardData = []
    const targetMetrics = ['Market Share', 'Supreme Volume', 'Office Volume']
    for(let metric of targetMetrics){
      let captions = ['', ''];
      let percentChangeStr = '';
      let valueKey = 'YTD ' + metric


      if (comparableMonth) {
        captions = [finalMonth['Date'], comparableMonth['Date']];
        let percentChangeInt = calculatePercentChange(valueKey, finalMonth, comparableMonth);
        percentChangeStr = formatPercentAsString(percentChangeInt, 0);
      } else if(finalMonth) {
        captions = [finalMonth['Date'], '']
      } else {
        captions = []
      }

      let valueYTD = finalMonth ? printPercent(finalMonth[valueKey]) : '';
      let valueComp = comparableMonth ? printPercent(comparableMonth[valueKey]) : '';

      _cardData.push([metric, valueYTD, valueComp, percentChangeInt.toFixed(1) + '%', captions])
    }
    setCardData(_cardData)
  })



  return (
    <Grid container spacing={3}>
      {cardData.map(card => (
        <Grid item xs={4}>
          <DataBox
            title={card[0]}
            valueYTD={card[1]}
            valueComp={card[2]}
            differenceValue={card[3]}
            captions={card[4]}
            period={'2020 Year End'}
            />
        </Grid>
      ))}
    </Grid>

  );
}
