import { makeStyles } from '@material-ui/core';

export default makeStyles(theme => ({
  root: {
    marginBottom: '3%'
  },
  difference: {
    marginTop: theme.spacing(1),
    display: 'flex',
    // textAlign: 'right',
  },
  differenceValue: {
    color: props =>
      props.positiveDifference ? theme.palette.success.main : theme.palette.error.main,
    // marginLeft: '5%',
    fontWeight: 'bolder',
    fontSize: '30px',
    marginTop: '-.1rem',
    marginLeft: '-1.5rem',
    fontFamily: 'Ubuntu',
    textAlign: 'right',
  },
  top: {
    fontFamily: 'Roboto',
    fontWeight: 'medium',
    fontSize: '14px',
  },
  title: {
    fontFamily: 'Roboto',
    fontWeight: 'bold',
    fontSize: '18px',
    margin: '0 0 0 0'

  },
  currentValue: {
    marginRight: '3%'
  },
  center: {
    width: '50%',
    margin: 'auto',
    // padding: '10.5px',
  },
  data: {
    margin: '0 2rem 0 2rem',
  }
}));
