import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {Card, Grid} from '@material-ui/core'
import { Header, colors } from 'tabler-react';
import Box from '@material-ui/core/Box';
import {Card as TCard} from 'tabler-react';
import Typography from '@material-ui/core/Typography';

const data = require('../../../data/experience.json')

const useStyles = makeStyles((theme) => ({
  header: {
    color: '#EE231A',
    fontWeight: 'bold',
    fontSize: '54px',
    paddingTop: '30px'
  },
  subtitle: {
    fontWeight: 'bold',
    fontSize: '23px',
  },
  text: {
    flexWrap: 'wrap',
    maxWidth: '60px'
  }
}));

export default function ExperienceGrid(props) {
  const classes = useStyles();
  return (
    <Grid container spacing={3}>
      {data.map(stat => (
          <Grid xs={6} padding={3}>
            <Box padding={1.5} paddingBottom={0.5} >
            <Card elevation={0} align={'center'} width={400}>
                <Header className={classes.header}>
                  {stat['Header']}
                </Header>
                <Typography className={classes.subtitle}>
                  {stat['Subtitle']}
                </Typography>
              <p className={classes.text}>
                {stat['text']}
              </p>
                <Box height={200}>

                </Box>

            </Card>
            </Box>

          </Grid>
      ))}
    </Grid>
  );
}
