import { makeStyles } from '@material-ui/core/styles';

export const useStyles = makeStyles((theme) => ({
  root: {
    // paddingTop: '',
  },
  card: {
    marginTop: '2.5%'
  },
  title: {
    textAlign: 'center',
    fontWeight: 'bold',
    fontSize: '40px',
    margin: '25px 0 -4rem 0'
  },
  subtitle: {
    fontWeight: 'bold',
    fontSize: '28px',
    textAlign: 'left',
    marginLeft: '5px',
    color: '#4C4C4C',
    marginBottom: '10px'
  },
  display: {
    textAlign: 'center',
    fontWeight: 'bold',
    fontSize: '50px',
    marginTop: '5px',
    color: '#50D55A',
    // margin: '25px 0 -4rem 0'
  },
  overviewText: {
    fontWeight: 'bold',
    fontSize: '20px'
  },
  overview: {
    margin: '0 -2.5rem 1.5rem -2.5rem',
  },
  overviewCard: {
    borderRadius: 30,
  }
}));
