import { makeStyles } from '@material-ui/core/styles';

export const useStyles = makeStyles((theme) => ({
  tableHead: {
    fontSize: '20px'
  },
  tableItem: {
    fontSize: '23px'
  },
  card: {
    marginTop: '2.5%',
  },
  title: {
    fontSize: '34px',
    fontWeight: 'light',
    fontFamily: 'Fira Sans',
    margin: '1.5rem 0 0 0',
    textAlign: 'center'
  },
  // title: {
  //   // textAlign: 'center',
  //   fontWeight: 'bold',
  //   fontSize: '22px',
  //   margin: '1rem -4rem 0',
  //   // color: 'White',
  //   // backgroundColor: '#7A9EDB',
  //   textAlign: 'center',
  //   borderRadius: '10em 10em 10em'
  // },
  subtitle: {
    fontWeight: 'bold',
    fontSize: '35px',
    position: 'relative',
    marginLeft: '1rem',
    marginRight: '3rem',
    color: 'rgba(35,39,50,0.93)',
    marginTop: '25px',
    textAlign: 'left',
    // backgroundColor: '#d4deea',
    // borderRadius: '30em 30em 60em 60em'
  },
  display: {
    textAlign: 'center',
    fontWeight: 'bold',
    fontSize: '40px',
    color: 'white',
  },
  overviewCard: {
    textAlign: 'center',
    borderRadius: '50%',
    margin: 'auto',
    height: '110px',
    width: '110px',
    backgroundColor: '#72AEF7',
  },
  overviewText: {
    fontWeight: 'bold',
    fontSize: '21px',
    textAlign: 'center',
    paddingBottom: '10px',

  },
  overview: {
    margin: '0 -2.5rem 1rem -2.5rem',
  },
  circle: {
    borderRadius: '100%',
    // backgroundColor: '#7A9EDB',
    height: '85px',
    width: '85px',

  },
  center: {
    width: '50%',
    margin: 'auto',
    padding: '10.5px',
  }
}));
